import React from 'react';
import { Modal, Backdrop, Fade, Paper, Typography, TextField, Button, FormControl, InputLabel, Select, MenuItem } from '@mui/material';
import { makeStyles } from '@mui/styles';

const useStyles = makeStyles((theme) => ({
  modal: {
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
  },
  modalPaper: {
    backgroundColor: theme.palette.background.paper,
    boxShadow: theme.shadows[5],
    padding: theme.spacing(3),
    width: '400px',
  },
  form: {
    display: 'flex',
    flexDirection: 'column',
    gap: theme.spacing(2),
  },
  formControl: {
    minWidth: 120,
  },
}));

const UsuarioForm = ({ open, handleClose }) => {
  const classes = useStyles();

  const handleSave = () => {
    // conectar al api
    handleClose();
  };

  return (
    <Modal
      open={open}
      onClose={handleClose}
      className={classes.modal}
      aria-labelledby="transition-modal-title"
      aria-describedby="transition-modal-description"
      closeAfterTransition
      BackdropComponent={Backdrop}
      BackdropProps={{
        timeout: 500,
      }}
    >
      <Fade in={open}>
        <Paper className={classes.modalPaper}>
          <Typography variant="h6" gutterBottom>
            Nuevo Usuario
          </Typography>
          <form className={classes.form} onSubmit={handleSave}>
            <TextField label="Nombre" variant="outlined" required />
            <TextField label="Apellido" variant="outlined" required />
            <TextField label="Usuario" variant="outlined" required />
            <TextField label="ContraseÃ±a" variant="outlined" type="password" required />
            <FormControl variant="outlined" className={classes.formControl} required>
              <InputLabel>Rol</InputLabel>
              <Select label="Rol">
                <MenuItem value="vendedor">Vendedor</MenuItem>
                <MenuItem value="administrador">Administrador</MenuItem>
              </Select>
            </FormControl>
            <Button type="submit" variant="contained" color="primary">
              Guardar
            </Button>
          </form>
        </Paper>
      </Fade>
    </Modal>
  );
};

export default UsuarioForm;
